mapper.select:
SELECT COUNT(*)
	FROM PROJECT A 
	JOIN MINORCATEGORY B ON A.MINORCODE = B.MINORCODE
	JOIN MIDCATEGORY C ON B.MIDCODE = C.MIDCODE
	WHERE 1=1

	AND PROJECTSTATCODE = #{projectStatCode}


	AND C.MAJORCODE = #{majorCode}


	AND C.MIDCODE = #{midCode}


	AND PROJECTTITLE LIKE '%'||#{keyword}||'%'

SELECT A.*, B.MINORNAME, C.*, F.MEMBERNICK, F.MEMBEREMAIL, nvl((select sum((e.fundPrice*d.packageamount)+d.extramoney) from fundinglog d join fundingoption e on d.packageNo = e.packageNo where d.projectNo = a.projectNo),0) as reach, nvl((select sum((e.fundPrice*d.packageamount)+d.extramoney) from fundinglog d join fundingoption e on d.packageNo = e.packageNo where d.projectNo = a.projectNo)/goalprice*100,0) as reachRate, nvl((select count(*) from fundinglog f where f.projectNo = a.projectNo),0) as funderNo FROM PROJECT A JOIN MINORCATEGORY B ON A.MINORCODE = B.MINORCODE JOIN MIDCATEGORY C ON B.MIDCODE = C.MIDCODE JOIN MEMBER F ON A.MEMBEREMAIL = F.MEMBEREMAIL WHERE 1=1
AND PROJECTSTATCODE = #{projectStatCode}
AND C.MAJORCODE = #{majorCode}
AND C.MIDCODE = #{midCode}
AND PROJECTTITLE LIKE '%'||#{keyword}||'%'
ORDER BY ${orderby} DESC
